$(function(){$(".dropdown-toggle").click(function(){$(".dropdown-menu").toggle()});$(".arrow-next").click(function(){var e=$(".active-slide");var t=e.next();var n=$(".active-dot");var r=n.next();if(t.length===0){t=$(".slide").first();r=$(".dot").first()}e.fadeOut(600).removeClass("active-slide");t.fadeIn(600).addClass("active-slide");n.removeClass("active-dot");r.addClass("active-dot")});$(".arrow-prev").click(function(){var e=$(".active-slide");var t=e.prev();var n=$(".active-dot");var r=n.prev();if(t.length===0){t=$(".slide").last();r=$(".dot").last()}e.fadeOut(600).removeClass("active-slide");t.fadeIn(600).addClass("active-slide");n.removeClass("active-dot");r.addClass("active-dot")})})(jQuery)