$(function(){var e,t,n;var r,i,s;var o;$(".next").click(function(){if(o)return false;o=true;e=$(this).parent();t=$(this).parent().next();$("#progressbar li").eq($("fieldset").index(t)).addClass("active");t.show();e.animate({opacity:0},{step:function(n,o){s=1-(1-n)*.2;r=n*50+"%";i=1-n;e.css({transform:"scale("+s+")"});t.css({left:r,opacity:i})},duration:800,complete:function(){e.hide();o=false},easing:"easeInOutBack"})});$(".previous").click(function(){if(o)return false;o=true;e=$(this).parent();n=$(this).parent().prev();$("#progressbar li").eq($("fieldset").index(e)).removeClass("active");n.show();e.animate({opacity:0},{step:function(t,o){s=.8+(1-t)*.2;r=(1-t)*50+"%";i=1-t;e.css({left:r});n.css({transform:"scale("+s+")",opacity:i})},duration:800,complete:function(){e.hide();o=false},easing:"easeInOutBack"})});$("[data-toggle=popover]").popover()})(jQuery)