$(document).ready(function(){var e,t,n;var r,i,s;var o;$(".next").click(function(){var n=$("#email").val();var u=$("#username").val();var a=$("#password").val();var f=$("#password1").val();var l=$("#firstname").val();var c=$("#lastname").val();if(n!=""&&u!=""&&a!=""&&f!=""){if(/[a-zA-Z0-9_]+([.][a-zA-Z0-9_]+)*@[a-zA-Z0-9_]+([.][a-zA-Z0-9_]+)*[.][a-zA-Z]{1,5}/.test(n)){var h=a.length;if(a==f&&h>=4){$("#lastname").css({background:"white"});if(o)return false;o=true;e=$(this).parent();t=$(this).parent().next();$("#progressbar li").eq($("fieldset").index(t)).addClass("active");t.show();e.animate({opacity:0},{step:function(n,o){s=1-(1-n)*.2;r=n*50+"%";i=1-n;e.css({transform:"scale("+s+")"});t.css({left:r,opacity:i})},duration:800,complete:function(){e.hide();o=false},easing:"easeInOutBack"})}else{$("#password1").css({background:"#F22"});alert("password do not match")}}}});$(".next1").click(function(){var n=$("#firstname").val();var u=$("#lastname").val();if(n!=""&&u!=""){if(o)return false;o=true;e=$(this).parent();t=$(this).parent().next();$("#progressbar li").eq($("fieldset").index(t)).addClass("active");t.show();e.animate({opacity:0},{step:function(n,o){s=1-(1-n)*.2;r=n*50+"%";i=1-n;e.css({transform:"scale("+s+")"});t.css({left:r,opacity:i})},duration:800,complete:function(){e.hide();o=false},easing:"easeInOutBack"})}});$(".previous").click(function(){if(o)return false;o=true;e=$(this).parent();n=$(this).parent().prev();$("#progressbar li").eq($("fieldset").index(e)).removeClass("active");n.show();e.animate({opacity:0},{step:function(t,o){s=.8+(1-t)*.2;r=(1-t)*50+"%";i=1-t;e.css({left:r});n.css({transform:"scale("+s+")",opacity:i})},duration:800,complete:function(){e.hide();o=false},easing:"easeInOutBack"})});$("[data-toggle=popover]").popover()})(jQuery)